import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";

class Movies extends Component {
	state = {
		movies: getMovies(),
	};

    renderMoviesTable(){
        if (this.state.movies.length === 0) {
            return <p>There are no movies in the database</p>
        }
        else{
            return (
                <table className="table">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Genre</th>
                            <th>Stock</th>
                            <th>Rate</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.movies.map((movie) => (
                            <tr key={movie._id}>
                                <td key={movie.title}>{movie.title}</td>
                                <td key={movie.genre.name}>{movie.genre.name}</td>
                                <td key={movie.numberInStock}>{movie.numberInStock}</td>
                                <td key={movie.dailyRentalRate}>{movie.dailyRentalRate}</td>
                                <td><button onClick={this.deleteMovie(movie._id)} className="button is-success is-inverted">Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            );
        }
    }
    // Delete movie
    deleteMovie = (id) => {
        const movies = this.state.movies.filter(movie => movie._id !== id);
        // this.setState({ movies });
    };

	render() {
		return (
			<React.Fragment>
				<div className='table-container'>
					{this.renderMoviesTable()}
				</div>
			</React.Fragment>
		);
	}
}

export default Movies;
