import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";

class Movies extends Component {
	state = {
		movies: getMovies(),
	};

	handleDelete = (movie) => {};

	render() {
		return (
			<React.Fragment>
				<div className='table-container'>
					<table className='table'>
						<thead>
							<tr>
								<th>Title</th>
								<th>Genre</th>
								<th>Stock</th>
								<th>Rate</th>
								<th>Actions</th>
							</tr>
						</thead>
						<tbody>
							{/* Call map function for each movie */}
							{this.state.movies.map((movie) => (
								<tr key={movie._id}>
									<td key={movie.title}>{movie.title}</td>
									<td key={movie.genre.name}>{movie.genre.name}</td>
									<td key={movie.numberInStock}>{movie.numberInStock}</td>
									<td key={movie.dailyRentalRate}>{movie.dailyRentalRate}</td>
									<td>Actions</td>
								</tr>
							))}
						</tbody>
					</table>
				</div>
			</React.Fragment>
		);
	}
}

export default Movies;
